INSERT INTO `cms_article_template_copy1` VALUES ('3', '3', '48,49', '1', 'markbro', '<fieldset style=\"font-family: sans-serif; border: 0px none;\"><h2 style=\"margin:8px 0px 0px;padding:0px;font-size:16px;line-height:28px;max-width:100%;color:#0070c0;min-height:32px;border-bottom-width:2px;border-bottom-style:solid;border-bottom-color:#0070c0;text-align:justify;\"><span class=\"autonum\" placeholder=\"1\" style=\"border-radius:80% 100% 90% 20%;color:#ffffff;display:block;float:left;line-height:20px;margin:0px 8px 0px 0px;max-width:100%;padding:4px 10px;word-wrap:break-word !important;background-color:#0070c0;\">1</span><strong data-brushtype=\"text\">第一标题sd</strong>\r\n &nbsp; &nbsp;</h2></fieldset><fieldset style=\"font-family: sans-serif; border: 0px none;\"><blockquote style=\"border-width:1px 1px 1px 5px;border-style:solid;border-color:#eeeeee #eeeeee #eeeeee #9f887f;border-radius:3px;padding:10px;margin:10px 0px;\"><h4 style=\"color:#9f887f;font-size:18px;margin:5px 0px;border-color:#9f887f;\">标题文字</h4><section data-style=\"color:inherit;font-size:14px;\" style=\"color:inherit;font-size:14px;line-height:20px;\"><p>内容描述.</p></section></blockquote></fieldset>', 'public', '1', '0', '0', '2019-12-31 10:38:25', '0', null, null, null, null);
INSERT INTO `cms_article_template_copy1` VALUES ('4', '4', '48,49', '1', 'markbro', '<fieldset style=\"font-family: sans-serif; border: 0px none;\"><section style=\"color:inherit;padding:5px 10px 0px 35px;border-left-width:2px;border-left-style:dotted;border-left-color:#e4e4e4;margin-left:27px;\"><section class=\"autonum\" style=\"width:32px;height:32px;color:#cafbd7;text-align:center;line-height:32px;border-radius:16px;background:#0eb83a;margin-left:-53px;margin-top:23px;\">1</section><section style=\"color:inherit;padding-bottom:10px;margin-top:-30px;\"><p style=\"clear:both;line-height:1.5em;font-size:14px;color:inherit;\"><span style=\"color:inherit;font-size:16px;\"><strong class=\"135title\" style=\"color:inherit;\">如何进入【微信+编辑器】？</strong></span>\r\n &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</p><p style=\"clear:both;line-height:1.5em;font-size:14px;color:inherit;\">网页搜索“微信+编辑器”，点击第一条搜索结果即可进入编辑器页面</p></section></section></fieldset>', 'public', '1', '0', '0', '2019-12-31 10:38:25', '0', null, null, null, null);
INSERT INTO `cms_article_template_copy1` VALUES ('5', '5', '48,49', '1', 'markbro', '<fieldset style=\"font-family:sans-serif;border:0px none;\"><fieldset style=\"margin:2em 1em 1em;padding:0px;border:0px #ffb3a7;border-image-source:none;max-width:100%;box-sizing:border-box;color:#3e3e3e;line-height:25px;word-wrap:break-word !important;\"><section style=\"max-width:100%;box-sizing:border-box;line-height:1.4;word-wrap:break-word !important;margin-left:-0.5em;\"><section style=\"max-width:100%;box-sizing:border-box;border-color:#000000;padding:3px 8px;border-radius:4px;color:#a71700;font-size:1em;display:inline-block;-webkit-transform:rotatez(-10deg);transform:rotate(-10deg);transform-origin:left center 0px;-webkit-transform-origin:0% 100% 0px;word-wrap:break-word !important;background-color:#ffb3a7;\"><span data-brushtype=\"text\" style=\"color:#ffffff;\">微信+编辑器</span>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</section></section><section data-style=\"line-height:24px;color:rgb(89, 89, 89); font-size:14px\" style=\"max-width:100%;box-sizing:border-box;padding:22px 16px 16px;border:1px solid #ffb3a7;color:#000000;font-size:14px;margin-top:-24px;\"><p style=\"line-height:24px;\"><span style=\"color:#595959;\">微信+编辑器提供非常好用的微信图文编辑器。可以随心所欲的变换颜色调整格式，更有神奇的自动配色方案。</span>\r\n &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</p></section></fieldset></fieldset>', 'public', '1', '0', '0', '2019-12-31 10:38:25', '0', null, null, null, null);
INSERT INTO `cms_article_template_copy1` VALUES ('6', '6', '48,49', '1', 'markbro', '<fieldset style=\"font-family:sans-serif;border:0px none;\"><fieldset style=\"margin:0px;padding:5px;border:1px solid #cccccc;max-width:100%;color:#3e3e3e;line-height:24px;text-align:justify;box-shadow:#a5a5a5 5px 5px 2px;background-color:#fafafa;\"><legend style=\"margin:0px 0px 0px 20px;padding:0px;text-align:left;width:auto;\"><strong><strong style=\"color:#666666;line-height:20px;background-color:#fffff5;\"><span data-brushtype=\"text\" style=\"border-radius:0.5em 4em 3em 1em 0.5em 2em;box-shadow:#a5a5a5 4px 4px 2px;color:white;max-width:100%;padding:4px 10px;text-align:justify;background-color:red;\">公告通知</span></strong></strong>\r\n &nbsp; &nbsp; &nbsp; &nbsp;</legend><section data-style=\"margin-top:2px; margin-bottom:0px; padding:0px; max-width:100%; min-height:1.5em; line-height:2em;font-weight:bold;\"><p style=\"padding:0px;max-width:100%;min-height:1.5em;line-height:2em;margin-top:0px;margin-bottom:0px;\">各位小伙伴们，微信图文美化编辑器正式上线了，欢迎大家多使用多提供反馈意见。使用<span style=\"color:inherit;\"><strong>谷歌与火狐浏览器</strong></span>，可获得与手机端一致的显示效果。ie内核的低版本浏览器可能有不兼容的情况</p></section></fieldset></fieldset>', 'public', '1', '0', '0', '2019-12-31 10:38:25', '0', null, null, null, null);
