DROP TABLE IF EXISTS `cms_comment`;
CREATE TABLE `cms_comment` (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT '主键',
  `pid` int(11) DEFAULT NULL COMMENT '父ID',
  `tid` varchar(50) DEFAULT NULL COMMENT '目标ID',
  `num_pei` smallint(6) DEFAULT '0' COMMENT '呸数',
  `num_penzi` smallint(6) DEFAULT '0' COMMENT '喷子数',
  `num_dou` smallint(6) DEFAULT '0' COMMENT '逗个数',
  `num_geili` smallint(6) DEFAULT '0' COMMENT '给力数',
  `type` varchar(50) DEFAULT 'article' COMMENT '评论类型',
  `user_id` varchar(50) DEFAULT NULL COMMENT '用户id',
  `user_name` varchar(50) DEFAULT NULL COMMENT '用户名称',
