-- Table structure for cms_pv
-- ----------------------------
DROP TABLE IF EXISTS `cms_pv`;
CREATE TABLE `cms_pv` (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT '主键',
  `uid` varchar(50) DEFAULT NULL COMMENT '用户ID',
  `module` varchar(50) DEFAULT NULL COMMENT '模块',
  `browser` varchar(50) DEFAULT NULL COMMENT '浏览器',
  `referer` varchar(255) DEFAULT NULL COMMENT 'referer',
  `os` varchar(50) DEFAULT NULL COMMENT '操作系统',
  `page_id` varchar(255) DEFAULT NULL COMMENT '页面内容ID',
  `url` varchar(255) DEFAULT NULL,
  `device_type` varchar(50) DEFAULT NULL,
  `time_zone` varchar(10) DEFAULT NULL COMMENT '时区',
  `ip` varchar(20) DEFAULT NULL COMMENT 'ip地址',
  `location` varchar(255) DEFAULT NULL COMMENT '地址',
  `create_time` datetime DEFAULT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=259 DEFAULT CHARSET=utf8;

-- ----------------------------
-- Records of cms_pv
-- ----------------------------

-- ----------------------------
-- Table structure for cms_resource
-- ----------------------------
DROP TABLE IF EXISTS `cms_resource`;
CREATE TABLE `cms_resource` (
  `id` varchar(64) NOT NULL COMMENT '资源ID',
  `resource_region` varchar(50) DEFAULT NULL COMMENT '资源专区',
  `title` varchar(100) NOT NULL COMMENT '资源名称',
  `type` varchar(10) NOT NULL COMMENT '资源类型',
  `description` varchar(255) DEFAULT NULL COMMENT '资源描述',
  `detail` longtext COMMENT '详情',
  `size` varchar(20) DEFAULT NULL COMMENT '资源大小',
  `save_path` varchar(255) DEFAULT NULL COMMENT '保存路径',
  `cover_image` varchar(255) DEFAULT NULL COMMENT '缩略图',
  `audit_state` varchar(20) DEFAULT '0' COMMENT '审核状态 0待审批2未通过1通过',
  `score` smallint(6) DEFAULT NULL COMMENT '评分 1-5',
  `download_type` varchar(20) DEFAULT '0' COMMENT '下载类型 0直接下载 1登录后下载 2关注下载',
  `favourite_count` int(11) DEFAULT '0' COMMENT '收藏数',
  `download_count` int(11) DEFAULT '0' COMMENT '下载次数',
  `hit` int(11) DEFAULT '0' COMMENT '点击数',
  `up_vote` int(11) DEFAULT '0' COMMENT '点赞数',
  `free` smallint(6) DEFAULT NULL COMMENT '付费 0免费 1收费',
  `pay_type` varchar(50) DEFAULT NULL COMMENT '付费类型 1 积分 2扫码支付',
  `cost` int(11) DEFAULT '0' COMMENT '花费多少积分',
  `tag_ids` varchar(255) DEFAULT NULL COMMENT '标签',
  `user_id` varchar(50) DEFAULT NULL COMMENT '上传人用户ID',
  `user_name` varchar(255) DEFAULT NULL COMMENT '上传人',
  `create_time` datetime DEFAULT NULL COMMENT '上传日期',
  `update_time` datetime DEFAULT NULL COMMENT '上传者 id',
  `status` smallint(6) DEFAULT '0' COMMENT '状态 0 正常 1停用',
  `remark` varchar(255) DEFAULT NULL COMMENT '备注',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

-- ----------------------------
-- Records of cms_resource
-- ----------------------------

-- ----------------------------
-- Table structure for cms_short_words
-- ----------------------------
DROP TABLE IF EXISTS `cms_short_words`;
CREATE TABLE `cms_short_words` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `short_words` varchar(255) DEFAULT '' COMMENT '短语',
  `tagIds` varchar(100) DEFAULT NULL COMMENT '标签',
  `user_id` varchar(20) DEFAULT NULL COMMENT '用户ID',
  `words_count` int(11) DEFAULT NULL COMMENT '字数',
  `weight` int(11) DEFAULT '0' COMMENT '权重',
  `audit_state` smallint(6) DEFAULT NULL COMMENT '审核状态',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=8 DEFAULT CHARSET=utf8;

-- ----------------------------
-- Records of cms_short_words
-- ----------------------------

-- ----------------------------
-- Table structure for cms_site_msg
-- ----------------------------
DROP TABLE IF EXISTS `cms_site_msg`;
CREATE TABLE `cms_site_msg` (
  `id` bigint(20) NOT NULL AUTO_INCREMENT COMMENT 'ID',
  `from_id` varchar(50) DEFAULT NULL COMMENT '发送者ID',
  `from_name` varchar(50) DEFAULT NULL COMMENT '发送者名称',
  `to_id` varchar(50) DEFAULT NULL COMMENT '接受者ID',
  `to_name` varchar(50) DEFAULT NULL COMMENT '接受者名称',
  `msg_type` varchar(50) DEFAULT NULL COMMENT '消息类别',
  `content` text COMMENT '消息内容',
  `create_time` datetime DEFAULT NULL COMMENT '消息时间',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=2 DEFAULT CHARSET=utf8;

-- ----------------------------
-- Records of cms_site_msg
-- ----------------------------

-- ----------------------------
-- Table structure for cms_tags
-- ----------------------------
DROP TABLE IF EXISTS `cms_tags`;
CREATE TABLE `cms_tags` (
  `tag_id` int(11) NOT NULL AUTO_INCREMENT COMMENT 'ID',
  `tag_type` varchar(50) DEFAULT NULL COMMENT '标签类型，如s系统标签，p个人标签',
  `user_id` varchar(50) DEFAULT NULL COMMENT '谁增加的该标签',
  `tag_name` varchar(50) DEFAULT NULL COMMENT '分类名称',
  `sort` int(11) DEFAULT NULL COMMENT '排序',
  `status` smallint(6) DEFAULT '0' COMMENT '状态 0正常 1停用',
  `url` varchar(255) DEFAULT NULL,
  PRIMARY KEY (`tag_id`)
) ENGINE=InnoDB AUTO_INCREMENT=50 DEFAULT CHARSET=utf8;

-- ----------------------------
-- Records of cms_tags
-- ----------------------------

-- ----------------------------
-- Table structure for cms_template
-- ----------------------------
DROP TABLE IF EXISTS `cms_template`;
CREATE TABLE `cms_template` (
  `template_id` int(11) NOT NULL AUTO_INCREMENT COMMENT 'ID',
  `template_code` varchar(50) DEFAULT NULL COMMENT '模板代码',
  `template_type` varchar(50) DEFAULT NULL COMMENT '模板分类',
  `template_name` varchar(255) DEFAULT NULL COMMENT '模板名称',
  `template_content` text COMMENT '模板内容',
  PRIMARY KEY (`template_id`)
) ENGINE=InnoDB AUTO_INCREMENT=3 DEFAULT CHARSET=utf8;

